mapper.insert:
INSERT INTO BOARD
(
board_seq
, emp_seq
, board_category
, board_title
, board_content
, board_orgname
, board_savname
)
VALUES
(
board_seqs.nextval
, #{emp_seq}
, #{board_category}
, #{board_title}
, #{board_content}
, #{board_orgname}
, #{board_savname}
)

mapper.update:
update board
set board_hitcount = board_hitcount+1
where board_seq = #{board_seq}

mapper.delete:
DELETE board
WHERE
   board_seq = #{board_seq}
and
   emp_seq = #{emp_seq}

mapper.update:
UPDATE board
SET
board_title = #{board_title}
, board_content = #{board_content}
, board_regdate = sysdate

  , board_orgname = #{board_orgname}
  , board_savname = #{board_savname}

WHERE
      board_seq = #{board_seq}
  and
     emp_seq = #{emp_seq}

update
   BOARD_COMMENTS
set
   BOARD_COMMENTS_CONTENT = #{board_comments_content}
where
   board_comments_seq = #{board_comments_seq}
